// import { showSnackbar } from "./app";
import { createSlice } from "@reduxjs/toolkit";
import axios from "../../utils/axios";
import { API_LEVEL } from "../../config";
import { ShowsnackBar } from "../actions";
// import { ShowsnackBar } from "../actions";
// import { showSnackbar } from "./app";
// import { showSnackbar } from "./app";

const initialState = {
  loading: false,
  error: null,
  post: null,
};

const slice = createSlice({
  name: "post",
  initialState,
  reducers: {
    NewPostRequest: (state) => {
      state.loading = true;
    },
    PostCreateSuccess: (state, action) => {
      state.loading = false;
      state.post = action.payload;
    },
    PostCreateFail: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

    // get the post of following user
    PostFollowingRequest: (state) => {
      state.loading = true;
    },
    PostFollowingSuccess: (state, action) => {
      state.loading = false;
      state.post = action.payload;
    },
    PostFollowingFail: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    PostFollowingReset: (state, action) => {
      state.post = []
    },
    
  },
});

// export the slice of reducer
export default slice.reducer;
export const addNewPost = (postData) => async (dispatch) => {
  try {

   
      dispatch({ type: 'NewPostRequest' });
      const config = { header: { "Content-Type": "application/json" } }
      // const { data } = await axios.post("/api/v1/post/new", postData, config);
      await  axios
      .post(`${API_LEVEL}/post/createpost`, postData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
        withCredentials:true
      })
      .then((res) => {
        console.log(res);
        dispatch({
          type: 'PostCreateSuccess',
          payload: res.data,
      });
      })
      .catch((err) => {
        console.log(err);
      });

  } catch (error) {
      dispatch({
          type: 'PostCreateFail',
          payload: error.response.data.message,
      });
  }
}
export const getPostsOfFollowing = (page = 1) => async (dispatch) => {

  try {

    dispatch(
      slice.actions.PostFollowingRequest
    );


    setTimeout(async () => {

        const { data } = await axios.get(`/api/v1/posts?page=${page}`);

        dispatch(
          slice.actions.PostFollowingSuccess({
            payload: data,
          })
        );
  

    }, 300);

} catch (error) {
  console.log(error)
  dispatch(
    slice.actions.PostFollowingFail({
      payload: error.message,
    })
  );

}



}
