import { createSlice, current } from '@reduxjs/toolkit';
export const userData =(data,id,newUserData)=>{
    console.log(newUserData.following.length)
    if((data).following.length < 1){
        return newUserData
    }else{
          const newData = current(data).following.map(item=>item === id ? newUserData :data)
    console.log(newData)
    return newData
    }
  
    // console.log(current(data))
}
