import React from 'react'
import { Link, useNavigate } from 'react-router-dom';
import { profileIcon, savedIcon, settingsIcon, switchAccountIcon } from './SvgIcons'
// import { logoutUser } from '../../actions/userAction';
import { useDispatch, useSelector } from 'react-redux';
import { toast } from "react-toastify";
import { ClickAwayListener } from '@mui/material';

const ProfileDetails = ({ setProfileToggle }) => {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const { user } = useSelector((state) => state.auth);

    const tabs = [
        {
            title: "Profile",
            icon: profileIcon,
            redirect: `/${user.username}`
        },
        {
            title: "Saved",
            icon: savedIcon,
            redirect: `/${user.username}`
        },
        {
            title: "Settings",
            icon: settingsIcon,
            redirect: "/accounts/edit"
        },
        {
            title: "Switch Account",
            icon: switchAccountIcon,
            redirect: "/"
        },
    ]

    const handleLogout = () => {
        // dispatch(logoutUser());
        navigate("/login");
        toast.success("Logout Successfully");
    }


    return (
        <ClickAwayListener onClickAway={() => setProfileToggle(false)}>
            <div className='absolute w-56 bg-white dark:bg-gray-500 h-[10rem] rounded drop-shadow top-14 right-0 md:right-3 border'>
             <div>
                
             </div>
            </div>

        </ClickAwayListener>

    )
}

export default ProfileDetails
