// import { showSnackbar } from "./app";
import { createSlice } from "@reduxjs/toolkit";
import axios from "../../utils/axios";
import { API_LEVEL } from "../../config";
import { ShowsnackBar} from "../actions";
// import { ShowsnackBar } from "../actions";
// import { showSnackbar } from "./app";
// import { showSnackbar } from "./app";


const initialState = {
  isAuthenticated: false,
  loading: true,
  error:null,
  user:null,
};

const slice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    LoadUserRequest:(state)=>  {
      state.loading = true;
    },
    LoadUserSuccess:(state, action)=>  {
      state.isAuthenticated = true;
      state.loading = false;
      state.user = action.payload;
    },
    LoadUserFail:(state, action)=> {
      state.loading = false;
      state.error = action.payload;
      state.isAuthenticated = false;
    },
    LoadShopRequest: (state) => {
      state.loading = true;
    },
    
    Login(state,action){
      state.isAuthenticated = true;
      state.loading = false;
      state.user = action.payload;
    }
  
  },
  
});

// export the slice of reducer

export default slice.reducer;

//  The Actions
export const loadUser = () => async (dispatch) => {
  try {
    dispatch({
      type: "LoadUserRequest",
    });


     await axios.get(`${API_LEVEL}/user/getuser`, {
      withCredentials: true,
    }).then(function(response){
      dispatch(
        slice.actions.LoadUserSuccess({
          payload: response.data.user
        })
     );
  
      dispatch( ShowsnackBar({ severity: "success", message: response.data.message}))

    }).catch(function (error) {
      dispatch({
        type: "LoadUserFail",
        payload: error,
      });
      dispatch( ShowsnackBar({ severity: "error", message: error.message }))
     
    });
   
 
  } catch (error) {
    dispatch({
      type: "LoadUserFail",
      payload: error,
    });
    dispatch( ShowsnackBar({ severity: "error", message: error.message }))
  }
}


export const loadShop = () => async (dispatch) => {

  try {
    dispatch({
      type: "LoadShopRequest",
    });
      await axios.get(`${API_LEVEL}/shop/getShop`, {
      withCredentials: true,
    }).then((response)=>{

     
          dispatch(
      slice.actions.LoadShopSuccess({
        payload: response.data.shopData
      }))

    dispatch( ShowsnackBar({ severity: "success", message: 'Seller Fetched'}))
    });

  } catch (error) {
    dispatch({
      type: "LoadShopFail",
      payload: error.message,
    });
   
    dispatch( ShowsnackBar({ severity: "error", message: error.message}))
  }




}




export const LoginUser = (formValues) => async (dispatch) => { 

  try {
    await axios
    .post(
      `${API_LEVEL}/auth/login`,
      {
        ...formValues,
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
        withCredentials: true,
      }
    )
    .then(function (response) {
     
      dispatch(
         slice.actions.LoadUserSuccess({
           payload: response.data.user
         })
      );

      console.log(response.data)

      dispatch( ShowsnackBar({ severity: "success", message: response.data.message }))
    
    })
    .catch(function (error) {
      dispatch({
        type: "LoadUserFail",
        payload: error,
      });
      dispatch( ShowsnackBar({ severity: "error", message: error.message }))
     
    });
  } catch (error) {
    dispatch({
      type: "LoadUserFail",
      payload: error,
    });
  }
}
