import { Dialog, LinearProgress } from "@mui/material";
import Picker from "@emoji-mart/react";
import React, { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
// import { addNewPost, clearErrors } from '../../actions/postAction';
// import { NEW_POST_RESET } from '../../constants/postConstants';
import { getImage } from "../../utils/constants";
import { emojiIcon } from "./SvgIcons";

const NewPost = ({ newPost, setNewPost }) => {
  const [dragged, setDragged] = useState(false);
  const [postImage, setPostImage] = useState("");
  const [postPreview, setPostPreview] = useState("");
  const {dark}= useSelector(state=> state.app.common)

  const handleDragChange = () => {
    setDragged(!dragged);
  };
  const handleFileChange = (e) => {
    const reader = new FileReader();
    setPostImage("");
    setPostPreview("");

    reader.onload = () => {
      if (reader.readyState === 2) {
        setPostPreview(reader.result);
        console.log(reader);
      }
    };
    reader.readAsDataURL(e.target.files[0]);
    setPostImage(e.target.files[0]);
  };

  return (
    <Dialog  open={newPost} onClose={() => setNewPost(false)} maxWidth='2xl'>
      <div className="flex flex-col  dark:bg-gray-600 max-w-4xl justify-between">
        <div className="bg-white py-3 border-b flex justify-between w-full dark:bg-gray-600 px-2">
          <span className="dark:text-white"> Create New Post</span>
          <button className="text-blue-500 font-medium">Share</button>
        </div>
        {/* {<LinearProgress></LinearProgress>} */}

        <div className="flex sm:flex-row sm:items-start items-center flex-col w-full">
          {postImage ? (
            <div className="bg-black h-48 sm:h-[80vh] w-full rounded-md ">
              <img
                draggable="false"
                className="object-contain h-full w-full"
                src={postPreview}
                alt="post"
              />
            </div>
          ) : (
            <div className="flex sm:flex-row sm:items-start items-center flex-col w-full ">
              <div
                onDragEnter={handleDragChange}
                onDragLeave={handleDragChange}
                className={`${
                  dragged && "opacity-40"
                } relative bg-white dark:bg-gray-600 h-36 sm:h-[80vh] w-full flex flex-col gap-2 items-center justify-center mx-16`}
              >
                {}
                <p className="text-xl dark:text-white">Drag photos and videos here</p>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="absolute h-full w-full opacity-0 dark:bg-gray-600"
                />
              </div>
            </div>
          )}

          <div className="flex flex-col sm:h-[80h] w-full bg-white dark:bg-gray-500 dark:shadow-md m-3 rounded-md">
            <div className="flex gap-3 px-3 py-2 items-center m-3">
              <img
                draggable="false"
                className="w-11 h-11 rounded-full object-cover"
                src={getImage()}
                alt="avatar"
              ></img>
              <span className="text-black text-sm font-semibold dark:text-white">Mr X</span>
            </div>

            <div className="m-3">
              <label className="block mb-4">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="block w-full text-sm text-slate-500
                                    file:mr-3 file:py-2 file:px-6
                                    file:rounded-full file:border-0
                                    file:text-sm file:cursor-pointer file:font-semibold
                                    file:bg-purple-100 file:text-purple-700
                                    hover:file:bg-purple-200
                                    "
                />
              </label>
              <textarea
                className="outline-none rounded-md resize-none h-32 sm:h-auto dark:bg-gray-500 w-[80%] m-9 dark:text-white dark:shadow-md px-2"
                placeholder="Write a caption..."
                name="caption"
                cols="40"
                rows="12"
                // value={caption}
                // onChange={(e) => setCaption(e.target.value)}
                // onClick={() => setShowEmojis(false)}
              ></textarea>
            </div>
            <div className="flex items-center justify-between w-[30rem] h-[1rem]">
            <button  className="bg-primary-blue text-white px-6 py-1.5 rounded font-medium hover:drop-shadow-lg uppercase text-sm tracking-wider w-full">Post</button>

            </div>
          </div>
        </div>

      </div>
    </Dialog>
  );
};

export default NewPost;
