import { createSlice } from "@reduxjs/toolkit";
import { dispatch } from "../store";
import { ClosesnackBar, ShowsnackBar, ToggleSideBar } from "../actions";
import axios from "../../utils/axios";
import { API_LEVEL } from "../../config";

// ----------------------------------------------------------------------

const initialState = {
  isLoading: true,
  products:null,
  error:null
};

const slice = createSlice({
  name: "product",
  initialState,
  reducers: {
    productCreateRequest: (state) => {
      state.isLoading = true;
    },
    productCreateSuccess: (state, action) => {
      state.isLoading = false;
      state.products = action.payload;
      state.success = true;
    },
    productCreateFail: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
      state.success = false;
    },
    productUpdateRequest: (state) => {
      state.isLoading = true;
    },
    productUpdateSuccess: (state, action) => {
      state.isLoading = false;
      state.products = action.payload;
      state.success = true;
    },
    productUpdateFail: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
      state.success = false;
    },

    // get all products of shop
    getAllProductsShopRequest: (state) => {
      state.isLoading = true;
    },
    getAllProductsShopSuccess: (state, action) => {
      state.isLoading = false;
      state.products = action.payload;
    },
    getAllProductsShopFailed: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },

    // delete product of a shop
    deleteProductRequest: (state) => {
      state.isLoading = true;
    },
    deleteProductSuccess: (state, action) => {
      state.isLoading = false;
      state.message = action.payload;
    },
    deleteProductFailed: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },

    // get all products
    getAllProductsRequest: (state) => {
      state.isLoading = true;
    },
    getAllProductsSuccess: (state, action) => {
      state.isLoading = false;
      state.products = action.payload;
    },
    getAllProductsFailed: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },

    clearErrors: (state) => {
      state.error = null;
    },
  },

  //   extraReducers(builder){
  //     builder.addCase(ShowsnackBar, (state,action)=>{
  //       state.snackbar.open = true;
  //       state.snackbar.severity = action.payload.severity;
  //       state.snackbar.message = action.payload.message;

  //     }).addCase(ClosesnackBar,(state,action)=>{
  //       state.snackbar.open = false;
  //       state.snackbar.message = null;
  //     })

  //     builder.addCase(ToggleSideBar,(state,action)=>{

  //       state.sidebar.sidebarBehaviour = !state.sidebar.sidebarBehaviour;
  //       state.sidebar.type = action.payload.type;
  //     })
  //   },
});

// Reducer
export default slice.reducer;

// ----------------------------------------------------------------------
//  Actions:
export const createProduct = (newForm) => async (dispatch) => {
  try {
    dispatch({
      type: "productCreateRequest",
    });

    const config = { headers: { "Content-Type": "multipart/form-data" } };

    await axios
      .post(
        `${API_LEVEL}/product/create-product`,

        newForm,
        {
          config,
          withCredentials: true,
        }
      )
      .then((response) => {
        dispatch(
          slice.actions.productCreateSuccess({
            payload: response.data.productSavingData,
          })
        );
      });
  } catch (error) {
    dispatch(
      slice.actions.productCreateFail({
        payload: error.message,
      })
    );

    console.log(error);
  }
};

export const getAllShopProduct = (id) => async (dispatch) => {


  try {
    dispatch({
      type: "getAllProductsShopRequest",
    });

    const { data } = await axios.get(
      `${API_LEVEL}/product/get-all-products-shop/${id}`
    );

    console.log(data)
    dispatch(
      slice.actions.getAllProductsShopSuccess({
        payload: data.productsData,
      }))
    
  } catch (error) {
    dispatch({
      type: "getAllProductsShopFailed",
      payload: error.message,
    });
  }
};
export const getAllProducts = (id) => async (dispatch) => {

  

  try {
    dispatch({
      type: "getAllProductsRequest",
    });

    const { data } = await axios.get(
      `${API_LEVEL}/product/get-all-products-shop/${id}`
    );

    console.log(data)
    dispatch(
      slice.actions.getAllProductsShopSuccess({
        payload: data.productsData,
      }))
    
  } catch (error) {
    dispatch({
      type: "getAllProductsShopFailed",
      payload: error.message,
    });
  }
};
export const updateProduct = (newForm) => async (dispatch) => {
  try {
    dispatch({
      type: "productUpdateRequest",
    });

    const config = { headers: { "Content-Type": "multipart/form-data" } };

    await axios
      .put(
        `${API_LEVEL}/product/update-product`,

        newForm,
        {
          config,
          withCredentials: true,
        }
      )
      .then((response) => {
        dispatch(
          slice.actions.productUpdateSuccess({
            payload: response.data.product,
          })
        );
      });
  } catch (error) {
    dispatch(
      slice.actions.productUpdateFail({
        payload: error.message,
      })
    );
    console.log(error);
  }
};

export const deleteProduct = (id) => async (dispatch) => { 

  try {
    dispatch({
      type: "deleteProductRequest",
    });

    const { data } = await axios.delete(
      `${API_LEVEL}/product/delete-shop-product/${id}`,
      {
        withCredentials: true,
      }
    );

    dispatch({
      type: "deleteProductSuccess",
      payload: data.message,
    });
  } catch (error) {
    dispatch({
      type: "deleteProductFailed",
      payload: error.message,
    });

    console.log(error)
  }


}
