// import { showSnackbar } from "./app";
import { createSlice } from "@reduxjs/toolkit";
import axios from "../../utils/axios";
import { API_LEVEL } from "../../config";
import { ShowsnackBar } from "../actions";
// import { ShowsnackBar } from "../actions";
// import { showSnackbar } from "./app";
// import { showSnackbar } from "./app";

const initialState = {
  loading: true,
  error: null,
  user: null,
  follow:false,
  posts:null,
};

const slice = createSlice({
  name: "userDetails",
  initialState,
  reducers: {
    LoadUserRequest: (state) => {
      state.loading = true;
    },
    LoadUserDetailsSuccess: (state, action) => {
     
      state.loading = false;
      state.user = action.payload.payload;
    },
    LoadUserFail: (state, action) => {
      state.loading = false;
      state.error = action.payload.payload;

    },
    FollowUserRequest: (state) => {
      state.loading = true;
    },
    FollowUserSuccess: (state, action) => {
     
      state.loading = false;
      state.user = action.payload.payload;
    },
    FollowUserFail: (state, action) => {
      state.loading = false;
      state.error = action.payload.payload;

    },

    // post crud
    NewPostRequest: (state) => {
      state.loading = true;
    },
    PostCreateSuccess: (state, action) => {
      state.loading = false;
      state.post = action.payload;
    },
    PostCreateFail: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },

  },
});

// export the slice of reducer
export default slice.reducer;

//  The Actions
export const getUserDetails = (username) => async (dispatch) => {
  try {

    console.log(username)
    dispatch({ type: 'LoadUserRequest' });
    const { data } = await axios.get( `${API_LEVEL}/user/userdetails/${username}`);

    console.log(data.user)
   
    dispatch(
      slice.actions.LoadUserDetailsSuccess({
        payload: data.user,
      })
    );

} catch (error) {
 
    dispatch({
        type: 'LoadUserFail',
        payload: error,
    });
}
};
// Follow | Unfollow User
export const followUser = (userId) => async (dispatch) => {
  try {

    dispatch(
      slice.actions.FollowUserRequest()
    );
      const { data } = await axios.get(`${API_LEVEL}/user/follow/${userId}`,{ withCredentials:true});

      console.log(data)
      dispatch(
        slice.actions.FollowUserSuccess({
          payload: data,
        })
      );

  } catch (error) {
    console.log(error)
    dispatch(
      slice.actions.FollowUserFail({
        payload: error.message,
      })
    );
  }
};
